INSERT INTO `django_admin_log` VALUES (418,'2018-06-25 05:47:27.213180','107','<p>Tim, an RBT, is at the supermarket when he sees a client that he regularly works with. He excitedly greets his client and talks about their most recent therapy session. After he heads home, he real','[{"changed": {"fields": ["task_list_item", "unit"]}}, {"changed": {"name": "choice", "object": "<p>He did nothing wrong - it would be unethical to treat a client different because he or she has a diagnosis.</p>", "fields": ["choice_text"]}}, {"changed": {"name": "choice", "object": "<p>He should have waited for the client to acknowledge him first, AND refrained from acknowledging any details about their therapy relationship unless explicit consent was given.</p>", "fields": ["choice_text"]}}, {"changed": {"name": "choice", "object": "<p>Tim should avoid speaking to clients in public, even if they acknowledge him first</p>", "fields": ["choice_text"]}}, {"changed": {"name": "choice", "object": "<p>He should have had the client sign a disclosure act notification form, which must be done each time you see a client in public</p>", "fields": ["choice_text"]}}]',12,1,2);
